import React from 'react';
import PropTypes from 'prop-types';
import Drawer from '@material-ui/core/Drawer';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { useText } from '../../theme/common';
import useStyles from './cards-style';

export default function General(props) {
  const classes = useStyles();
  const text = useText();
  const {
    img,
    title,
    desc
  } = props;

  const [state, setState] = React.useState({
    top: false,
    left: false,
    bottom: false,
    right: false,
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const renderContext = () => {
    return (
      <div >
        <header className="MuiPaper-root MuiAppBar-root MuiAppBar-positionFixed MuiAppBar-colorDefault mui-fixed MuiPaper-elevation4">
          <div className="MuiTabs-root jss841">
              <div className="MuiTabs-scroller MuiTabs-fixed" style={{
                overflow: "hidden"}}>
                <div className="MuiTabs-flexContainer MuiTabs-centered" role="tablist">
                    <button className="MuiButtonBase-root MuiTab-root MuiTab-textColorInherit MuiTab-labelIcon" tabindex="-1" type="button" role="tab" aria-selected="false">
                      <span className="MuiTab-wrapper jss846">
                          <svg className="MuiSvgIcon-root" focusable="false" viewBox="0 0 24 24" aria-hidden="true">
                            <path d="M12 22C6.49 22 2 17.51 2 12S6.49 2 12 2s10 4.04 10 9c0 3.31-2.69 6-6 6h-1.77c-.28 0-.5.22-.5.5 0 .12.05.23.13.33.41.47.64 1.06.64 1.67 0 1.38-1.12 2.5-2.5 2.5zm0-18c-4.41 0-8 3.59-8 8s3.59 8 8 8c.28 0 .5-.22.5-.5 0-.16-.08-.28-.14-.35-.41-.46-.63-1.05-.63-1.65 0-1.38 1.12-2.5 2.5-2.5H16c2.21 0 4-1.79 4-4 0-3.86-3.59-7-8-7z"></path>
                            <circle cx="6.5" cy="11.5" r="1.5"></circle>
                            <circle cx="9.5" cy="7.5" r="1.5"></circle>
                            <circle cx="14.5" cy="7.5" r="1.5"></circle>
                            <circle cx="17.5" cy="11.5" r="1.5"></circle>
                          </svg>
                          Theme
                      </span>
                      <span className="MuiTouchRipple-root"></span>
                    </button>
                    <button className="MuiButtonBase-root MuiTab-root MuiTab-textColorInherit Mui-selected MuiTab-labelIcon" tabindex="0" type="button" role="tab" aria-selected="true">
                      <span className="MuiTab-wrapper jss846">
                          <svg className="MuiSvgIcon-root" focusable="false" viewBox="0 0 24 24" aria-hidden="true">
                            <path d="M4 6H2v14c0 1.1.9 2 2 2h14v-2H4V6zm16-4H8c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V4c0-1.1-.9-2-2-2zm0 14H8V4h12v12zM10 9h8v2h-8zm0 3h4v2h-4zm0-6h8v2h-8z"></path>
                          </svg>
                          Explore
                      </span>
                      <span className="MuiTouchRipple-root"></span>
                    </button>
                </div>
                <span className="jss981 jss983 MuiTabs-indicator" style={{left: "300px", width: "160px"}}></span>
              </div>
          </div>
        </header>
      </div>
    )
  }

  return (
    <Paper className={classes.generalCard}>
      <figure>
        <img src={img} alt={title} />
      </figure>
      <Paper className={classes.desc}>
        <Typography variant="h6" gutterBottom>{title}</Typography>
        <Typography className={text.paragraph}>
          {desc}
        </Typography>
        <Button onClick={toggleDrawer("right", true)} className={classes.button} color="secondary" size="small" variant="contained">
          See Detail
        </Button>
      </Paper>
      <Drawer anchor={"right"} open={state["right"]} onClose={toggleDrawer("right", false)}>
       {
         renderContext()
       }
      </Drawer>
    </Paper>
  );
}

General.propTypes = {
  img: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  desc: PropTypes.string.isRequired,
};
