import {useMutation} from "@apollo/react-hooks";

import {MUTATION_CREATE_SUBSCRIPTION} from "./Support";

const SubscriptionController = () => {

    const [createSubscription, {loading, error}] = useMutation(MUTATION_CREATE_SUBSCRIPTION)

    if(loading) return null;
    if(error) console.log("error: ", error)

    return children({
        loading, createSubscription
    })
}

export default SubscriptionController;
